int sensorPin = A0; // kijelőljük, hogy melyik lábra kötjük a potenciómétert 
 int ledPin = 13; // kijelőljük a led melyik lábra van kötve
 int sensorValue = 0; // deklaráljuk a változót és értélet adunk neki 
 void setup() { 
 pinMode(ledPin, OUTPUT); //dekleráljuk a ledPin kivezetést kimenetként 
 }
 void loop() { 
 sensorValue = analogRead(sensorPin); //legyen egyenlő a sensorValue változó a sensorPin értékével 
 digitalWrite(ledPin, HIGH); // a ledPin legyen magas szinten 
 delay(sensorValue); // időzítés a beolvasot időig miliszekundumban 
 digitalWrite(ledPin, LOW); / a ledPin kimenet legyen alacsony szinten 
 delay(sensorValue); // időzítés a beolvasot időig miliszekundumban 
 }

